    private void enviarBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enviarBtnActionPerformed
        
        try {
            //Recogemos el valor de la caja de texto
            String texto = mensajeJTextField.getText();
            System.out.println("hola " + texto);
            
            //Construimos el Socket para la comunicaci√≥n
            Socket socket = new Socket("localhost", 6666);
            
            //Flujo de datos hacia el servidor
            OutputStream aux = socket.getOutputStream();
            DataOutputStream flujoSalida = new DataOutputStream(aux);//Flujo de salida
            
            flujoSalida.writeUTF(texto);
            
            //Cierro la conexion
            socket.close();
            
            
        } catch (IOException ex) {
            System.out.println(ex.getMessage());
        }
    }//GEN-LAST:event_enviarBtnActionPerformed

